// [Description] v1.1.0
// This code block creates sections from markdown content.
// It processes the input, generates sections, and outputs results directly within the 'data' object as JSON.
// It passes through all other top-level objects from $input.first().json unchanged.
// ======================================================

// =============================
// Configuration
// =============================

const CONFIG = {
  MAX_HEADER_LEVEL: 3,
  NEWLINE: "\n",
};

// =============================
// Input Variables
// =============================

// Get the content from the input node
const content = $input.first().json.node.contentInput;

// =============================
// Helper Functions
// =============================

/**
 * Generates sections from markdown content
 * @param {string} markdownContent - The markdown content to process
 * @returns {Array} - Array of sections
 */
function generateTocWithContent(content) {
  // Split content into lines and process each line
  const lines = content.split('\n');
  const sections = [];
  let currentSection = null;

  // Extract headers (up to h3) and their content
  const headerSections = lines.filter(line => {
    const headerMatch = line.match(/^#+/);
    return headerMatch && headerMatch[0].length <= CONFIG.MAX_HEADER_LEVEL;
  })
    .map((header, index) => {
      const headerLevel = header.match(/^#+/)[0].length;
      const text = header.replace(/^#+\s+/, '').trim();

      // Get content until next h1-h3 header or end
      const nextHeaderIndex = lines.findIndex((line, i) => {
        if (i <= lines.indexOf(header)) return false;
        const match = line.trim().match(/^#+/);
        return match && match[0].length <= 3;
      });

      const sectionContent = lines.slice(
        lines.indexOf(header) + 1,
        nextHeaderIndex === -1 ? undefined : nextHeaderIndex
      ).join(CONFIG.NEWLINE).trim(); // Trim to remove leading/trailing newlines

      return {
        itemTitle: text,
        contentInput: sectionContent,
        itemIndex: index + 1, // Starting at 1
        itemLevel: headerLevel
      };
    });

  return headerSections;
}

// =============================
// Main Execution Block
// =============================

let executionLog = [];

try {
  // Generate the sections
  const sections = generateTocWithContent(content);
  executionLog.push("Sections created successfully");

  // Update the data array with the generated sections
  $input.first().json.data = sections;
} catch (error) {
  executionLog.push(`Error creating sections: ${error.message}`);
}

// =============================
// Construct Output JSON
// =============================

const finalOutput = {
  ...$input.first().json,
  data: $input.first().json.data,
  code: {
    success: true,
    message: "Sections created successfully",
    log: executionLog,
  },
};

// =============================
// Return Output
// =============================

return [{ json: finalOutput }];

// ======================================================
// Clean and Create Image References v1.0.36
//
// This code processes Markdown content to find and extract image elements,
// collects image data into a 'refImages' array, and replaces each image
// in the content with a token [{image:N}], where N is the index of the image.
// All other Markdown elements, including links, remain unchanged.
//
// Steps:
// 1. Import required libraries.
// 2. Initialize input variables and configurations.
// 3. Iterate over each item in the input data array to create image references.
// 4. Replace images with tokens using regex based on the image sources.
// 5. Construct the final output, preserving all original input data.
// 6. Return the final output.
//
// IMPORTANT: By default, this code preserves ALL input data at ALL levels (including nested properties)
// from $input.first().json in the output, unless the development requirements explicitly specify otherwise.
// ======================================================

// =============================
// Import Required Libraries
// =============================

// No additional libraries are needed for the regex approach.

// =============================
// Input Variables and Initialization
// =============================

// Retrieve the first input item's JSON data.
const input = $input.first().json;

// Extract the 'data' array from the input. If it doesn't exist, initialize it as an empty array.
const dataArray = input.data || [];

// Initialize the image index outside the loop to ensure sequential indexing across all items.
let imageIndex = 1; // Starting index for images.

// Initialize the execution log, preserving any existing log entries.
let executionLog = input.code && input.code.log ? input.code.log : [];

// =============================
// Main Execution Block
// =============================

try {
  // Iterate over each item in the 'data' array to create image references.
  dataArray.forEach(item => {

    // ---------
    // Setup
    // ---------

    // Use 'contentInput' from the item as the base content for processing. If it doesn't exist, default to an empty string.
    const content = item.contentInput || '';

    // Initialize 'contentRefs' with 'contentInput'.
    item.contentRefs = content;

    // Ensure the 'refImages' array exists in the item to store image references.
    item.refImages = item.refImages || [];

    // ---------
    // Extracting Image References and Replacing with Tokens
    // ---------

    // Regular expression to match Markdown image syntax.
    const imageMarkdownRegex = /!\[([^\]]*)\]\(([^)\s]+)(?:\s+"([^"]*)")?\)/g;

    // Replace images in 'contentRefs' with tokens and collect references.
    item.contentRefs = item.contentRefs.replace(imageMarkdownRegex, (match, altText, src, title) => {
      // Create an image reference object with the extracted details.
      const imageRef = {
        type: 'image',
        index: imageIndex,
        content: {
          text: altText || '',
          source: src || '',
          title: title || ''
        }
      };

      // Append the image reference to the item's 'refImages' array.
      item.refImages.push(imageRef);

      // Log the extraction.
      executionLog.push(`Extracted image with index ${imageIndex} from item "${item.itemTitle || 'Untitled'}"`);

      // Increment the global image index for the next image.
      imageIndex++;

      // Return the replacement token with a space after it.
      return `[{image:${imageRef.index}}] `;
    });

    // Log the replacement.
    executionLog.push(`Replaced images with tokens in item "${item.itemTitle || 'Untitled'}"`);
  });

  // =============================
  // Constructing the Final Output
  // =============================

  // Use the spread operator to include all original top-level properties from the input.
  const finalOutput = {
    ...input,

    // Update the 'data' array with the processed items.
    data: dataArray,

    // Include the 'code' object with success status and execution log.
    code: {
      success: true,
      message: "All items processed successfully.",
      log: executionLog
    }
  };

  // =============================
  // Returning the Final Output
  // =============================

  // Return the final output as an array with a single JSON object.
  return [{ json: finalOutput }];

} catch (error) {
  // =============================
  // Error Handling
  // =============================

  // If an error occurs during processing, construct an error output.
  const errorOutput = {
    ...input,

    // Update the 'code' object with failure status and error message.
    code: {
      success: false,
      message: error.message,
      log: executionLog
    }
  };

  // Return the error output as an array with a single JSON object.
  return [{ json: errorOutput }];
}

// ======================================================
// Clean and Create Link References v1.2.2
//
// This code processes Markdown content to find and extract link elements,
// collects link data into a 'refLinks' array without overwriting existing entries,
// and replaces each link in the content with a token like 'text [{link:N}]',
// where N is the index of the link.
// All other Markdown elements remain unchanged.
//
// IMPORTANT:
// - Avoids nested comments to prevent syntax errors.
// - Uses a robust regex for link processing.
// - Preserves all existing 'contentRefs' and 'references' data.
// - Stores link references only in 'refLinks'.
// - Ensures that link indices are sequential across all items.
// - Follows all specified guidelines and requirements.
//
// Steps:
// 1. Initialize input variables and configurations.
// 2. Use robust regex to find and replace markdown links.
// 3. Update 'contentRefs' and append new link references to 'item.refLinks'.
// 4. Construct the final output, preserving all original input data.
// 5. Return the final output.
//
// ======================================================

// =============================
// Input Variables and Initialization
// =============================

// Original input data
const input = $input.first().json;

// Example input structure:
// {
//   auth: { /* authentication data */ },
//   data: [ /* array of items */ ],
//   code: { /* previous code execution data */ },
//   // other properties
// }

// Array of data items to process
const dataArray = input.data || [];

// Execution log array
let executionLog = input.code?.log || [];

// Initialize the global link index counter
let globalLinkIndex = 1;

// =============================
// Helper Functions
// =============================

/**
 * Escapes special characters in a string for use in a regular expression
 * @param {string} string - The string to escape
 * @returns {string} - The escaped string
 */
function escapeRegExp(string) {
  return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

// =============================
// Main Execution Block
// =============================

try {
  // Iterate over each item in the 'data' array
  dataArray.forEach((item) => {
    // Preserve existing 'references' array (e.g., images)
    item.references = Array.isArray(item.references) ? item.references : [];

    // Initialize or preserve 'refLinks' array
    item.refLinks = Array.isArray(item.refLinks) ? item.refLinks : [];

    // Use existing contentRefs if available, otherwise fall back to contentInput
    let content = (typeof item.contentRefs === 'string') ? item.contentRefs : (item.contentInput || '');

    // Log the initial state
    executionLog.push(`Processing item "${item.itemTitle || 'Untitled'}"`);

    // =============================
    // Link Processing with Robust Regex
    // =============================

    // Regex pattern to match markdown links
    const linkRegex = /\[([\s\S]*?)\]\(([\s\S]*?)\)/g;

    let matches;
    const replacements = [];

    // Find all links in the content
    while ((matches = linkRegex.exec(content)) !== null) {
      const fullMatch = matches[0]; // The full markdown link
      let linkText = matches[1]; // The text inside the brackets
      let linkUrl = matches[2]; // The URL inside the parentheses

      // Remove any surrounding whitespace from text and URL
      linkText = linkText.trim();
      linkUrl = linkUrl.trim();

      // Use the global link index for the new reference
      const linkIndex = globalLinkIndex;

      // Create link reference object
      const linkRef = {
        type: 'link',
        index: linkIndex,
        content: {
          text: linkText,
          source: linkUrl,
          title: '', // Title is not captured in standard markdown links
        },
      };

      // Add link reference to the 'item.refLinks' array
      item.refLinks.push(linkRef);

      // Increment the global link index counter
      globalLinkIndex++;

      // Construct the replacement text
      const replacementText = `${linkText} [{link:${linkIndex}}]`;

      // Prepare replacement
      replacements.push({
        originalText: fullMatch,
        replacementText: replacementText,
      });

      executionLog.push(`Found link: "${linkText}" with URL: "${linkUrl}"`);
    }

    // Perform replacements
    replacements.forEach(({ originalText, replacementText }) => {
      const escapedOriginal = escapeRegExp(originalText);
      const regex = new RegExp(escapedOriginal, 'g');
      content = content.replace(regex, replacementText);
    });

    // Update contentRefs with processed content
    item.contentRefs = content;

    // Log completion for the item
    executionLog.push(
      `Processed ${replacements.length} links in item "${item.itemTitle || 'Untitled'}"`
    );
  });

  // =============================
  // Constructing the Final Output
  // =============================

  const finalOutput = {
    ...input,        // Preserve all top-level input properties
    data: dataArray, // Contains all items with updated 'refLinks' and 'contentRefs'
    code: {
      success: true,
      message: 'All items processed successfully.',
      log: executionLog,
    },
  };

  // =============================
  // Return Output
  // =============================

  // Return the final output as an array with a single object
  return [{ json: finalOutput }];

} catch (error) {
  // =============================
  // Error Handling
  // =============================

  // Log error message
  executionLog.push(`Error: ${error.message}`);

  // Construct error output
  const finalOutput = {
    ...input,
    code: {
      success: false,
      message: error.message,
      log: executionLog,
    },
  };

  // Return the error output
  return [{ json: finalOutput }];
}

