// ======================================================
// HTML Content Cleaner & Metadata Extractor v1.0.4
// This code block cleans HTML content while preserving document structure and extracting metadata.
// It handles content cleaning, metadata extraction and outputs results directly within the 'data' object as JSON.
// ======================================================

// =============================
// Configuration
// =============================

const CONFIG = {
  PRESERVE_ELEMENTS: {
    a: ["href", "title"],
    img: ["src", "alt", "title"],
    table: ["class"],
    th: ["scope"],
    td: ["rowspan", "colspan"],
    code: ["class"],
    pre: ["class"],
    blockquote: ["cite"],
    cite: true,
    sup: true,
    sub: true,
    ul: true,
    ol: true,
    li: true,
    dl: true,
    dt: true,
    dd: true,
    math: true,
    figure: true,
    figcaption: true,
  },
  REMOVE_SELECTORS: [
    "script",
    "style",
    "iframe",
    "form",
    ".vector-menu",
    ".vector-header",
    ".mw-jump-link",
  ],
};

// =============================
// Input Variables
// =============================

// Get input items
const items = $input.all();
const contentHtml = items[0].json.data.contentHtml; // HTML content to clean
const preserveMetadata = items[0].json.data.preserveMetadata || true; // Flag to preserve metadata

// =============================
// Import Required Libraries
// =============================

const { JSDOM } = require("jsdom");
const { Readability } = require("@mozilla/readability");

// =============================
// Helper Functions
// =============================

/**
 * Extracts metadata from the HTML document
 * @param {Document} document - The HTML document
 * @returns {Object} - Extracted metadata
 */
function extractMetadata(document) {
  const metadata = {
    itemTitle: null,
    itemAuthor: null,
    description: null,
    language: null,
    datePublished: null,
    dateModified: null,
    contentMetrics: {
      wordCount: 0,
      hasImages: false,
      hasTables: false,
      hasCode: false,
      hasFormulas: false,
      sections: [],
      citations: [],
    },
  };

  try {
    // Extract basic metadata
    metadata.itemTitle =
      document.querySelector("title")?.textContent?.trim() ||
      document
        .querySelector('meta[property="og:title"]')
        ?.getAttribute("content") ||
      document.querySelector("h1")?.textContent?.trim();

    metadata.itemAuthor =
      document.querySelector('meta[name="author"]')?.getAttribute("content") ||
      document
        .querySelector('meta[property="article:author"]')
        ?.getAttribute("content");

    metadata.description =
      document
        .querySelector('meta[name="description"]')
        ?.getAttribute("content") ||
      document
        .querySelector('meta[property="og:description"]')
        ?.getAttribute("content");

    metadata.language = document.documentElement?.lang;

    // Extract dates
    metadata.datePublished = document
      .querySelector('meta[property="article:published_time"]')
      ?.getAttribute("content");
    metadata.dateModified = document
      .querySelector('meta[property="article:modified_time"]')
      ?.getAttribute("content");

    // Content metrics
    metadata.contentMetrics.wordCount = document.body.textContent
      .trim()
      .split(/\s+/).length;
    metadata.contentMetrics.hasImages =
      document.querySelectorAll("img").length > 0;
    metadata.contentMetrics.hasTables =
      document.querySelectorAll("table").length > 0;
    metadata.contentMetrics.hasCode =
      document.querySelectorAll("pre, code").length > 0;
    metadata.contentMetrics.hasFormulas =
      document.querySelectorAll(".math, .formula").length > 0;

    // Extract sections
    document.querySelectorAll("h1, h2, h3").forEach((heading) => {
      metadata.contentMetrics.sections.push({
        level: parseInt(heading.tagName[1]),
        title: heading.textContent.trim(),
      });
    });

    return metadata;
  } catch (error) {
    console.error("Error extracting metadata:", error);
    return metadata;
  }
}

/**
 * Cleans and structures HTML content
 * @param {string} html - Raw HTML content
 * @returns {Object} - Cleaned HTML and metadata
 */
function cleanHtmlStructure(html) {
  try {
    const dom = new JSDOM(html);
    const document = dom.window.document;

    // Extract metadata before cleaning
    const metadata = extractMetadata(document);

    // Remove unwanted elements
    CONFIG.REMOVE_SELECTORS.forEach((selector) => {
      document.querySelectorAll(selector).forEach((element) => {
        element.remove();
      });
    });

    // Clean attributes while preserving important ones
    document.querySelectorAll("*").forEach((element) => {
      const tagName = element.tagName.toLowerCase();
      const preserveConfig = CONFIG.PRESERVE_ELEMENTS[tagName];

      if (preserveConfig) {
        if (preserveConfig === true) return;

        const attributes = Array.from(element.attributes);
        attributes.forEach((attr) => {
          if (!preserveConfig.includes(attr.name)) {
            element.removeAttribute(attr.name);
          }
        });
      }
    });

    // Ensure proper document structure
    if (!document.querySelector("article")) {
      const article = document.createElement("article");
      const body = document.body;
      while (body.firstChild) {
        article.appendChild(body.firstChild);
      }
      body.appendChild(article);
    }

    return {
      html: document.documentElement.outerHTML,
      metadata,
    };
  } catch (error) {
    throw new Error(`Failed to clean HTML: ${error.message}`);
  }
}

// =============================
// Main Execution Block
// =============================

let executionLog = [];
let errors = [];

try {
  // Clean HTML and extract metadata
  const { html: cleanedHtml, metadata } = cleanHtmlStructure(contentHtml);
  executionLog.push('Successfully cleaned HTML and extracted metadata');

  // Convert cleaned HTML to Base64
  const base64Content = Buffer.from(cleanedHtml).toString('base64');
  executionLog.push('Converted cleaned HTML to Base64');

  // Process all items while preserving original data
  const processedItems = items.map(item => ({
    json: {
      ...item.json,
      data: {
        ...item.json.data,
        contentBase64: base64Content,  // Add Base64 content
        contentType: 'text/html',      // Add MIME type
        filename: 'content.html',      // Add filename
        metadata: {
          ...metadata,
          extractedAt: new Date().toISOString()
        }
      },
      code: {
        success: true,
        message: "Successfully cleaned HTML and converted to Base64",
        log: executionLog
      }
    }
  }));

  return processedItems;

} catch (error) {
  errors.push(error.message);
  return items.map(item => ({
    json: {
      ...item.json,
      code: {
        success: false,
        message: `Failed to process HTML: ${error.message}`,
        log: executionLog,
        errors
      }
    }
  }));
}

